(function($) {
	"use strict";
	return $(function() {

		var $grid = $('.grid-masonry').masonry({
			itemSelector: '.masonry-item',
			columnWidth: '.masonry-item:first-child',
			precentPosition: true
		});

		$grid.imagesLoaded().progress( function() {
			$grid.masonry('layout');
		});

		return;
	});
})(jQuery);
