(function($) {
	"use strict";
	return $(function() {
		$(document).foundation();
		// Smooth sticky header animations by toggling class names on sticky parent.
        $('.site-header div[data-sticky]').on('mutateme.zf.trigger', function(e){
        	var $parent = $(this.parentNode);
        	if ( this.className.indexOf('is-stuck') >= 0 ) {
        		$parent.addClass('child-stuck').removeClass('child-not-stuck')
        	} else {
        		$parent.removeClass('child-stuck').addClass('child-not-stuck');
        	}
        });

        $('#header-widgets').on('on.zf.toggler', function(e){
            $('#header-widgets').parents('[data-sticky-container]').addClass('search-open');
        });

        $('#header-widgets').on('off.zf.toggler', function(e){
            $('#header-widgets').parents('[data-sticky-container]').removeClass('search-open');
        });

		return;
	});
})(jQuery);
